binding.script:
var switch_pos = getprop("controls/switches/stby-batt");
if (switch_pos == 1) {
    setprop("controls/lighting/batt-test-lamp-norm", 0);
} else
if (switch_pos == 0) {
    setprop("controls/lighting/batt-test-lamp-norm", 1);  
}

var switch_pos = getprop("controls/switches/stby-batt");
if (switch_pos == 0) {
    settimer(func(){
        setprop("controls/switches/stby-batt", 1);
        setprop("controls/lighting/batt-test-lamp-norm", 0);
    }, 5);
}

var master_state = getprop("/controls/switches/master-bat");
if (!master_state) setprop("/controls/switches/master-alt", 0);
c172p.click("master");

var alt_state = getprop("/controls/switches/master-alt");
if (alt_state) setprop("/controls/switches/master-bat", 1);
c172p.click("master");

var power = getprop("/instrumentation/audio-panel/com1-btn");
if (power) {
    setprop("/instrumentation/comm/power-btn", 1);
    setprop("/instrumentation/comm/volume", 1);
} else {
    setprop("/instrumentation/comm/power-btn", 0);
    setprop("/instrumentation/comm/volume", 0);
}

var power = getprop("/instrumentation/audio-panel/com2-btn");
if (power) {
    setprop("/instrumentation/comm1/power-btn", 1);
    setprop("/instrumentation/comm1/volume", 1);
} else {
    setprop("/instrumentation/comm1/power-btn", 0);
    setprop("/instrumentation/comm1/volume", 0);
}
